#include <LiquidCrystal.h>#include <Wire.h>#include "radio.h"#include "RDA5807M.h"#include "RDSParser.h"// initialize the library with the numbers of the interface pinsLiquidCrystal lcd(12, 11, 5, 4, 3, 2);int scanKeypad(int delay);void selectRadiostationAtIndex(const short radio_index);short channel = 0;void _setup(){	Serial.begin(9600);	Wire.begin();                 // Инициализация двухпроводной шины I2C.  	// set up the LCD's number of columns and rows:  	lcd.begin(16, 2);  	// Print a message to the LCD.  	lcd.print("hello, world!");	//	turn lcd led backlight on  	pinMode(6, OUTPUT);  	digitalWrite(6, HIGH);  	//	configure keyboard input	for (int pin_in=7; pin_in<=10; ++pin_in) {   		pinMode(pin_in, INPUT);	}}void _loop(){	char buffer[16];	static int iii=0;	snprintf(buffer, sizeof(buffer), "%d %s", ++iii, "hello my beloved friends and enemies. how r u doing?");	Serial.println(buffer);	delay(500);}